import React from "react";
import {useQuery, gql, useApolloClient, ApolloClient} from '@apollo/client';
import styles from "./ProductCard.module.css"


const CARDS = gql`
    query GetCards($productId: String!) {
        categories {
            name
            products {
                id
                name
                inStock
                gallery
                description
                category
                brand
            }
        }
        category {
            name
        }
        product(id: $productId) {
            id
            name
            inStock
            gallery
            description
            category
            attributes {
                id
                name
                type
                items {
                    displayValue
                    value
                    id
                }
            }
            prices {
                currency {
                    label
                    symbol
                }
                amount
            }
            brand
        }
        currencies {
            label
            symbol
        }
    }
`;
ApolloClient
    .query({ variables: {input: {name: "ALL"}}, query:CARDS})
    .then((res) => console.log(res))

