import React from "react";
import {useEffect} from "react"
import {ReactComponent as VectorDown} from '../SVG/VectorCurrency.svg';
import {ReactComponent as VectorUp} from "../SVG/VectorUp.svg"
import {ReactComponent as CartQuantity} from "../SVG/cartQuantity.svg"

import {Link} from "react-router-dom";
import styles from "./Actions.module.css"
import {ReactComponent as HeaderBasket} from "../SVG/headerBasket.svg";

// const UseClickOutside = (handler) => {
//     let domeNode = React.createRef()
//
//     useEffect(() => {
//         let maybeHandler = (e) => {
//             if (!domeNode.current.contains(e.target)) {
//                 handler();
//                 // this.setState({
//                 //     openPopup: false,
//                 // })
//             }
//         }
//         document.addEventListener("mousedown", maybeHandler);
//
//         return () => {
//             document.removeEventListener("mousedown", maybeHandler)
//         }
//     })
//     return domeNode
// }

// let domNode = UseClickOutside(() => {
//     this.setState({
//         openPopup: false
//     })
// })

class Actions extends React.Component {
    constructor(props) {
        super(props);
        this.ref = React.createRef()
    }
           // refReact.createRef()

    handleClickOutside = ({target}) => {
               if(this.ref.current && !this.ref.current.contains(target)) {
                   console.log("click outside")

                   this.setState({
                       openPopup: false
                   })
                   // console.log(this.state.openPopup)
               } else {
                   console.log("click inside")
               }
    }

    componentDidMount() {
               document.addEventListener("mousedown", this.handleClickOutside)
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.openPopup !== this.props.openPopup) {

            console.log(this.props.openPopup)
            console.log("HELLO")
            this.handleClickOutside(this.ref)
        }
    }

    componentWillUnmount() {
               document.removeEventListener("mousedown", this.handleClickOutside)
    }

    // useClickOutside = (handler) => {
    //     // let domeNode = React.createRef()
    //
    //     // useEffect(() => {
    //     let maybeHandler = (e) => {
    //         if (!this.domeNode.current.contains(e.target)) {
    //             handler();
    //             this.setState({
    //                 openPopup: false,
    //             })
    //         }
    //
    //         document.addEventListener("mousedown", maybeHandler);
    //
    //         return () => {
    //             document.removeEventListener("mousedown", maybeHandler)
    //         }
    //     }

        // })

        // let maybeHandler = (e) => {
        //     if (!domeNode.current.contains(e.target)) {
        //         handler();
        //         // this.setState({
        //         //     openPopup: false,
        //         // })
        //     }
        //     document.addEventListener("mousedown", maybeHandler);
        //
        //     return () => {
        //         document.removeEventListener("mousedown", maybeHandler)
        //     }
        // }
    //
    //     return this.domeNode
    // }

    // componentDidUpdate(prevProps, prevState) {
        // console.log(this.props.openPopup)
        // if (prevProps.openPopup !== this.props.openPopup) {
        //
        //     this.useClickOutside()
        //     // document.addEventListener("mousedown", () => {
        //     //     this.setState({
        //     //         openPopup: false
        //     //     })
        //     // })
        // }
    // }

    render() {
        // console.log(this.props)
        const {
            currencies,
            activeCurrency,
            openPopup,
            onSelectCurrencies,
            onOpenPopup,
            toggleModal,
            totalCount
        } = this.props
        // const {activeCurrency, openPopup, selectedCurrency} = this.state
        // const selectedName = currencies[activeCurrency].currency.symbol
        // console.log(currencies[activeCurrency].currency.label)
        // let symbol = currencies[activeCurrency].currency.symbol
        // console.log(currencies[activeCurrency]?.currency.symbol)
        // console.log(onSelectCurrencies)
        // console.log(this.props.useClickOutside)
        // console.log(activeCurrency)
        // let domNode = UseClickOutside(() => {
        //     this.setState({
        //         openPopup: false
        //     })
        // })

        return (
            <div className={styles.actions}>
                <span className={styles.popupClosed}
                      onClick={onOpenPopup}>
                    {activeCurrency ? currencies[activeCurrency].currency.symbol : "$"}
                    <span>
                        {openPopup ? <VectorUp/> : <VectorDown/>}
                        </span>
                </span>
                <span className={styles.separator}></span>
                {
                    openPopup && (
                        <div>
                            <ul className={styles.blockCurrencies}
                                ref={this.ref}
                            >
                                {
                                    currencies?.map((item, index) => (
                                        <li key={`${item} ${index}`}
                                            onClick={(e) => onSelectCurrencies(index, e)}
                                            className={`${styles.actionsItem}  
                                            ${activeCurrency === index ? styles.active : ''}`}
                                        >
                                            <div>{item.currency.symbol}</div>
                                            <div>{item.currency.label}</div>
                                        </li>
                                    ))
                                }
                            </ul>
                        </div>
                    )
                }
                {/*<Link to="/cart">*/}

                <div className={styles.cart} onClick={toggleModal}>< HeaderBasket/></div>
                {totalCount ? (<div className={styles.cartQuantity}><CartQuantity/></div>) : ""}
                {totalCount ? (
                    <span className={styles.quantity}
                          style={totalCount > 9 ? {left: 54.5} : {left: 59}}
                    >{totalCount}
                    </span>
                ) : ""}


                {/*</Link>*/}
            </div>
        )
    }
}

export default Actions;
