import React from "react";
import styles from "./ProductCard.module.css"

class ProductCard extends React.Component {
    constructor(props) {
        super(props);
        this.state= {
           product: props.product,
            categories: props.categories
        }
    }

    render() {
        const { name, category, gallery} = this.props.product;
        console.log(this.name)
        return(
            <div className={styles.container}>
                <div className={styles.blockImg}>
                    <img className={styles.img} src={gallery[0]} alt="Photo product"/>
                </div>
                <div className={styles.content}>
                    <div className={styles.title}>Title:{name}</div>
                    <div className={styles.price}>Price:{category}{this.props.categories.name}</div>
                </div>
            </div>
        )
    }
};

export default ProductCard
