import React from "react";
import styles from "./Pdp.module.css"
import GroupTitle from "./GroupTitle";
import ChoiceSize from "./ChoiseSize";
import ChoiceColor from "./ChoiceColor";
import {apolloClient} from "../index";
import {gql} from "@apollo/client";

const GET_CARD = gql`
    query Product($productId: String!) {
        product(id: $productId) {
            id
            name
            inStock
            gallery
            description
            category
            prices {
                amount
                currency {
                    symbol
                    label
                }
            }
            brand
            attributes {
                id
                name
                type
                items {
                    id
                    value
                    displayValue
                }
            }
        }
    }
`;

class PDP extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            product: [this.renderPDP],
            // productId: ''

        }
    }

    renderPDP = async (id) => {
        try {
            const result = await apolloClient
            .query({
                query: gql`
                    query Product($productId: String!) {
                        product(id: $productId) {
                            id
                            name
                            inStock
                            gallery
                            description
                            category
                            prices {
                                amount
                                currency {
                                    symbol
                                    label
                                }
                            }
                            brand
                            attributes {
                                id
                                name
                                type
                                items {
                                    id
                                    value
                                    displayValue
                                }
                            }
                        }
                    }
                `
            }, {variables: {productId: id}});
            const product = result.data;

            this.setState({
                product: product,
                // cardId: this.state.cardId
            });

        } catch (err) {
            console.log(err);
        }
        ;
    }

    // handleLogin = async () => {
    //
    //     const response = await apolloClient.query({
    //         query: GET_CARD,
    //         variables: {
    //             // productId,
    //         },
    //     })
    // }

    componentDidMount() {
        this.renderPDP();
        // this.handleLogin()
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.productCards !== this.state.productCards) {
            console.log(this.state)
            this.renderPDP()
        }
    }

    render() {
        apolloClient
            .query({query: GET_CARD, variables: {productId: "xbox-series-s"}})
            .then((res) => {
                console.log(res)
                return (
                    <main className={styles.container}>
                        <section className={styles.blockImg}>
                            <img className={styles.blockImgItem} src="" alt="img1"/>
                            <img className={styles.blockImgItem} src="" alt="img2"/>
                            <img className={styles.blockImgItem} src="" alt="img3"/>
                        </section>
                        <section className={styles.blockGroup}>
                            <img className={styles.groupImg} src="" alt="img main"/>
                            <div className={styles.groupChoice}>
                                <div><GroupTitle/></div>
                                <div className={styles.choiceSize}><ChoiceSize/></div>
                                <div className={styles.choiceColor}><ChoiceColor/></div>
                                <div className={styles.groupChoicePrice}>
                                    <h5 className={styles.h5}>PRICE:</h5>
                                    <div className={styles.price}>$50.00</div>
                                </div>
                                <button className={styles.button}>ADD TO CART</button>
                                <p className={styles.description}>Description</p>
                            </div>
                        </section>
                    </main>
                )

            })

        // const handleLogin = async () => {
        //
        //     const response = await apolloClient.query({
        //         query: GET_CARD,
        //         variables: {
        //             cardId,
        //         },
        //     })
        // }
        // console.log(this.props)
        console.log(this.state)

        // return (
        //     <main className={styles.container}>
        //         <section className={styles.blockImg}>
        //             <img className={styles.blockImgItem} src="" alt="img1"/>
        //             <img className={styles.blockImgItem} src="" alt="img2"/>
        //             <img className={styles.blockImgItem} src="" alt="img3"/>
        //         </section>
        //         <section className={styles.blockGroup}>
        //             <img className={styles.groupImg} src="" alt="img main"/>
        //             <div className={styles.groupChoice}>
        //                 <div><GroupTitle/></div>
        //                 <div className={styles.choiceSize}><ChoiceSize/></div>
        //                 <div className={styles.choiceColor}><ChoiceColor/></div>
        //                 <div className={styles.groupChoicePrice}>
        //                     <h5 className={styles.h5}>PRICE:</h5>
        //                     <div className={styles.price}>$50.00</div>
        //                 </div>
        //                 <button className={styles.button}>ADD TO CART</button>
        //                 <p className={styles.description}>Description</p>
        //             </div>
        //         </section>
        //     </main>
        // )
    }
}

export default PDP
